import React, { useState, useEffect } from 'react';
import { Row, Button, Col, Label, Input, Card, CardBody, CustomInput } from 'reactstrap';

import { useHistory } from 'react-router';
import {
  Colxx,
  Separator,
} from '../../../../components/common/CustomBootstrap';
import Breadcrumb from '../../../../containers/navs/Breadcrumb';
import IntlMessages from '../../../../helpers/IntlMessages';
import { NotificationManager } from '../../../../components/common/react-notifications';


const BulkOrderOptions = ({ match }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [orderItems, setOrderItems] = useState([]);
  const [onchange, setOnchange] = useState();
  const [importFile, setImportFile] = useState(null);
  const [formData, setFormData] = useState({});
  const [externalLinkData, setExternalLinkData] = useState({});

  const history = useHistory();



  const onValueChange=(e) => {
    setOnchange(e)
    console.log(e, "---------------------------------------------e")
    history.push('/app/orders/import-bulk-orders');

  }

  const ImportFtp = () => {
      console.log(formData, "-----------------------------form Data")
  }

  const ImportExternalLink = (e) => {
       console.log(e.target.value, "===========================================External Link")     
}

  const handleAddClick = () => {
    setOrderItems([
      ...orderItems,
      {
        label: '',
        value: '',
        listPrice: '',
        costColumn1Price: '',
        unitOfMeasure: '',
        UPCRetail: '',
        qty: 1,
      },
    ]);
  };



  return isLoading ? (
    <div className="loading" />
  ) : (
    <>
      <Row>
        <Colxx xxs="12">
          <Breadcrumb heading={'Import Order'} match={match} />
          <div className="text-zero">{'  '}</div>

          <Separator className="mb-5" />
        </Colxx>
      </Row>

      <div className="d-flex flex-column">
        <Row className="mb-4">
        <Colxx xxs="3">
            <Card>
              <CardBody>
                <div className="h5">Select Options</div>
                <hr />
                <Col className="m-2 p-2">

                      <Input
                        type="radio"
                        value = "1"
                        name = "options"
                        onChange={() => {
                            onValueChange(1);
                          }}

                      /> 
                      <h6>Direct Upload</h6>
                </Col>
                <Col className="m-2 p-2">

                <Input
                type="radio"
                value = "2"
                name = "options"
                onChange={() => {
                    onValueChange(2);
                  }}

                /> 
                <h6>Using FTP/SSH</h6>
                </Col>
                <Col className="m-2 p-2">

                <Input
                type="radio"
                value = "3"
                name = "options"
                onChange={() => {
