/* eslint-disable no-unused-expressions */import mousetrap from'mousetrap';import{useEffect,useRef}from'react';/**
 * Use mousetrap hook
 *
 * @param  {(string | string[])} handlerKey - A key, key combo or array of combos according to Mousetrap documentation.
 * @param  { function } handlerCallback - A function that is triggered on key combo catch.
 */export default(function(handlerKey,handlerCallback){var actionRef=useRef(null);actionRef.current=handlerCallback;useEffect(function(){mousetrap.bind(handlerKey,function(evt,combo){typeof actionRef.current==='function'&&actionRef.current(evt,combo);evt.preventDefault();});return function(){mousetrap.unbind(handlerKey);};},[handlerKey]);});
