      description: formData.description,
    };
    console.log(data);
    try {
      const response = await axios.post('/api/inventory/notes', data);
      NotificationManager.success(
        response.data.success,
        'Success',
        3000,
        null,
        null,
        ''
      );
      formData.description = '';
      fetchNotes();
    } catch (err) {
      console.log(err);
    }
  };
  const deleteNote = async (id) => {
