                              setVendor={setVendor}
                              formData={formData}
                              setformData={setformData}
                              selectedVendors={selectedVendors}
                              setSelectedVendors={setSelectedVendors}
                            />
                          </Collapse>
                        </CardBody>
                      </Card>
                      <Card className="rounded-top mt-4">
                        <CardHeader className="bg-warning">
                          <CardTitle className="mt-3 mb-0 row">
                            <div className="text-left col-8">
                              <i className="iconsminds-unlock-2 h5 mx-2"></i>
                              SUB VENDOR DETAILS
                            </div>
                            <div className="text-right col-4">
                              <Button
                                color="primary"
                                onClick={() =>
                                  setCollapseSubVendor(!collapseSubVendor)
                                }
                                className="mb-1"
                              >
                                {collapseSubVendor ? (
                                  <IntlMessages id="Close" />
                                ) : (
                                  <IntlMessages id="Edit" />
                                )}
                              </Button>
                            </div>
                          </CardTitle>
                        </CardHeader>
                        <Separator />
                        <CardBody>
                          <Collapse isOpen={collapseSubVendor}>
                            <SubVendorDetailForm
                              vendors={subVendors}
                              vendor={subVendor}
                              setVendor={setSubVendor}
                              formData={formData}
                              setformData={setformData}
                            />
                          </Collapse>
                        </CardBody>
                      </Card>
                    </Colxx>
                  </Row>
                </Colxx>
              </CardBody>
            </Card>
          </Colxx>
        </Row>
      </div>
      <Modal isOpen={typeModal} toggle={() => setTypeModal(!typeModal)}>
        <ModalHeader>
          <IntlMessages id="Add New Product Type" />
        </ModalHeader>
        <ModalBody>
          <div>
            <Label>
              <IntlMessages id="Name" />
            </Label>
            <Input
              onChange={(e) => {
                setName(e.target.value);
              }}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <Button onClick={() => alert('Added')} color="primary">
            Add
          </Button>{' '}
          <Button color="secondary" onClick={() => setTypeModal(false)}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
      {/* Notes Modal */}
      <Modal isOpen={open} toggle={() => setOpen(!open)}>
        <ModalHeader>
          <IntlMessages id="Notes" />
        </ModalHeader>
        <ModalBody>
          <div className="mb-3">
            <Label>
              <IntlMessages id="Note Type" />
            </Label>
            <Select
              components={{ Input: CustomSelectInput }}
              className="react-select"
              classNamePrefix="react-select"
              name="form-field-name"
              options={selectData}
              onChange={(e) => {
                setformData({ ...formData, reprensiable: e.value });
              }}
            />
          </div>
          {notes.length ? (
            notes.map((note) => (
              <div class="alert alert-info alert-dismissible fade show">
                {note.description}
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  onClick={() => deleteNote(note.id)}
                >
                  <span aria-hidden="true">
                    <i className="simple-icon-trash text-danger"></i>
                  </span>
                </button>
              </div>
            ))
          ) : (
            <div className="text-center">
              <h1>
                {' '}
                <i className="iconsminds-notepad h1"></i>{' '}
              </h1>
              <h6>No Notes to display</h6>
            </div>
          )}
        </ModalBody>
        <ModalFooter>
          <Input
            type="textarea"
            value={formData.description}
            placeholder="Add Note"
            onChange={(e) => {
              setformData({ ...formData, description: e.target.value });
            }}
          />
          <Button color="success" className="ml-2" onClick={addNote}>
            Add
          </Button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default EditProduct;
