function authHeader() {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.token) {
      return { 'token': user.token };
    } else {
      return {};
    }
  }

const getCurrentUser = () => {
    return JSON.parse(localStorage.getItem("user"));
  };

export default {
    authHeader,
    getCurrentUser
}
